getDocuments.script:
fetch(
  'https://us-east-1.console.aws.amazon.com/billing/rest/api-proxy/p2s?debugOnlyOperation=GetTransactionsHistory',
  {
    headers: {
      accept: 'application/json, text/plain, */*',
      'content-type': 'application/json',
      'sec-fetch-mode': 'cors',
      'sec-fetch-site': 'same-origin',
      'x-awsbc-xsrf-token': '{{xsrfToken}}',
    },
    referrer: 'https://us-east-1.console.aws.amazon.com/billing/home',
    referrerPolicy: 'strict-origin-when-cross-origin',
    body: '{"contentString":"{\\"dashboardDataSource\\":\\"Live\\",\\"entityTypes\\":[\\"Payment\\",\\"CreditMemo\\"],\\"fields\\":[\\"transactionDate\\",\\"invoiceId\\",\\"paymentMethod\\",\\"type\\",\\"currency\\",\\"amount\\",\\"billingPeriod\\",\\"issuer\\",\\"systemIdentifier\\",\\"chargeType\\",\\"purchaseOrderId\\",\\"payerAccountId\\"],\\"filters\\":[{\\"__type\\":\\"com.amazonaws.awspaymentportalservice#ByDate\\",\\"type\\":\\"ByDate\\",\\"startDate\\":\\"2010-07-06T00:00:00.000Z\\",\\"endDate\\":\\"2030-10-04T23:59:59.999Z\\"}],\\"locale\\":\\"en-US\\"}","headers":{"Content-Encoding":"amz-1.0","Content-Type":"application/json","X-Amz-Target":"AWSPaymentPortalService.GetTransactionsHistory"},"method":"POST","path":"/","region":"us-east-1"}',
    method: 'POST',
    mode: 'cors',
    credentials: 'include',
  },
)
  .then((res) => res.json())
  .then((d) => {
    return d.entities.map((i) => ({
      id: i.fields.invoiceId.value,
      date: new Date(i.fields.transactionDate.value).toISOString(),
      total: i.fields.amount.renderedValue,
      currency: i.fields.currency.value,
      groupId: i.fields.systemIdentifier?.value?.groupId,
    }))
  })
  .then(JSON.stringify)

then.script:
(function (transactions) {
  function generateMonthsFromTransactions(transactions) {
    const uniqueYearMonths = new Set()

    transactions.forEach((transaction) => {
      const date = new Date(transaction.date)
      const yearMonth = `${date.getFullYear()}-${date.getMonth() + 1}`
      uniqueYearMonths.add(yearMonth)
    })

    return Array.from(uniqueYearMonths).map((yearMonth) => {
      const [year, month] = yearMonth.split('-')
      return { year: parseInt(year), month: parseInt(month) }
    })
  }

  return generateMonthsFromTransactions(transactions)
})(JSON.parse('{{allInvoices}}'))

forEach.script:
fetch(
  'https://us-east-1.console.aws.amazon.com/billing/rest/v1.0/taxinvoice/newmetadatav2?year={{months.year}}&month={{months.month}}',
  {
    headers: {
      'content-type': 'application/json',
      'x-awsbc-xsrf-token': '{{xsrfToken}}',
    },
    referrer: 'https://us-east-1.console.aws.amazon.com/billing/home',
    referrerPolicy: 'strict-origin-when-cross-origin',
    body: null,
    method: 'GET',
    mode: 'cors',
    credentials: 'include',
  },
)
  .then((res) => res.json())
  .then((a) => {
    return a.timsTaxInvoiceMetadatas.map((da) => ({
      id: da.taxInvoiceId,
      filePath: da.s3FileKey,
      invoiceId: da.commercialInvoiceId,
      externalInvoiceId: da.externalInvoiceId,
    }))
  })
  .then((x) => x.flatMap((a) => a))

fetch(
