getConfigOptions.script:
fetch('https://www.figma.com/api/user/plans', {
  headers: {
    'content-type': 'application/json',
  },
  body: null,
  method: 'GET',
  mode: 'cors',
  credentials: 'include',
})
  .then((r) => r.json())
  .then((r) =>
    r.meta?.teams.map((team) => ({
      label: team.name,
      value: team.id,
    })),
  )

getDocuments.script:
fetch('https://www.figma.com/api/subscriptions/team/{{targetTeamId}}/invoices')
  .then((res) => res.json())
  .then((res) => {
    if (res.error) {
      throw new Error(res.message)
    }
    return res.meta.map((invoice) => invoice.hosted_invoice_url)
  })
