getDocuments.script:
fetch('https://business.revolut.com/api/subscriptions/history/periods', {
  headers: {
    accept: 'application/json, text/plain, */*',
    'x-device-id': '{{revoDeviceId}}',
    'x-timezone': 'Europe/Vienna',
  },
  referrer: 'https://business.revolut.com/',
  referrerPolicy: 'origin',
  body: null,
  method: 'GET',
  mode: 'cors',
  credentials: 'include',
})
  .then((r) => r.json())
  .then((r) => r.filter((i) => i.fee && i.fee.amount > 0))
  .then((r) =>
    r.map((item) => ({
      amount: item.fee.amount / 100 + ' ' + item.fee.currency,
      date: new Date(item.from),
      from: item.from,
    })),
  )

forEach.script:
fetch(
  'https://business.revolut.com/api/subscriptions/invoice?from={{billingHistory.from}}&format=pdf',
  {
    headers: {
      'x-device-id': '{{revoDeviceId}}',
    },
  },
)
  .then((response) => response.blob())
  .then((blob) => {
    const url = window.URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = '{{revoDeviceId}}.pdf' // or get filename from Content-Disposition header
    document.body.appendChild(a)
    a.click()
    window.URL.revokeObjectURL(url)
    document.body.removeChild(a)
  })
