forEach.script:
fetch('https://riders.uber.com/graphql', {
  headers: { 'content-type': 'application/json', 'x-csrf-token': 'x' },
  referrerPolicy: 'strict-origin-when-cross-origin',
  body: JSON.stringify({
    query: `{ invoiceFiles(tripUUID: \"{{ride.uuid}}\") { files { downloadURL __typename } __typename } getTrip(tripUUID: \"{{ride.uuid}}\") { trip { uuid beginTripTime fare status } } }`,
  }),
  method: 'POST',
  mode: 'cors',
  credentials: 'include',
})
  .then((res) => res.json())
  .then((res) =>
    res?.data
      ? {
          id: res.data.getTrip.trip.uuid,
          invoiceUrl: res.data.invoiceFiles.files[0]?.downloadURL,
          date: res.data.getTrip.trip.beginTripTime,
          total: res.data.getTrip.trip.fare,
        }
      : {},
  )
